import logo from './logo.svg';
import './App.css';
import { useState } from 'react';
import { useEffect } from 'react';
import { useRef } from 'react';
import React from 'react';
import { ethers } from 'ethers';
import { loadTasks, addTask, removeTask, completeTask } from './_logics'


function App() {
  const [tasks, setTasks] = useState([]);
  const [accounts, setAccounts] = useState([]);
  const [wallet, setWallet] = useState(null);

  const newTaskTitle = useRef(0);

  let provider;
  let contract;

  const contractAdress = '0xEe6d504b7ca4FBd10d2d953Ed441A287e25Be57F';
  const contractAbi = [
      'function addTask(string memory title) public',
      'function removeTask(int id, int rType) public',
      'function getTasks() public view returns (tuple(string, int)[] memory)'
  ]


  useEffect(()=>{
    console.log(window.ethereum);
  }, [])


  const connectWallet = async()=>{
    provider = new ethers.providers.Web3Provider(window.ethereum);
    const tempAccounts = await provider.send('eth_requestAccounts', []);
    contract = new ethers.Contract(contractAdress, contractAbi, provider);

    setAccounts(tempAccounts);

    setTasks(await loadTasks());
    console.log(tasks);

  }


  return (
    <div className="App">
      <h1>DApp todo List | </h1> 
      <h4>current account : {accounts[0]}</h4>
      <button onClick={connectWallet}>Connect</button>
      <div>
        <input ref={newTaskTitle} placeholder='enter task'/> 
        <button onClick={()=>{}}>add task</button>
      </div>
      <div>
        <h3>tasks: </h3>
        {
          // tasks.map(task => <h4>{task[0]}</h4>)
        }
      </div>
    </div>
  );
}

export default App;
