      "type": "function"
    },
    {
      "inputs": [
        {
          "internalType": "address",
          "name": "userAddress",
          "type": "address"
        }
      ],
      "name": "userExists",
      "outputs": [
        {
          "internalType": "bool",
          "name": "",
          "type": "bool"
        }
      ],
      "stateMutability": "view",
      "type": "function"
    }
  ]
  const userContractAbi =[
    {
      "inputs": [
        {
          "internalType": "address",
          "name": "userAdress",
          "type": "address"
        }
      ],
      "stateMutability": "nonpayable",
