    if (tasks.length != 0) {
      setTasksRepresentation(formatTasks(tasks, contractWithSigner));
    }
  }, [tasks])

  useEffect( ()=>{
    if (walletRegistered == false ) {
      setRegisterWalletButton(<button onClick={registerWallet}>Register Wallet</button>)
    } else if (walletRegistered == true) {
      setRegisterWalletButton(null);
    }
  }, [walletRegistered])

  const connectWallet = async (provider) => {
    const currentAccounts = await provider.send('eth_requestAccounts', []);
    setAccounts(currentAccounts);
  }


  const registerWallet = async () => {
    contractWithSigner.addUser();
  }



  return (
    <div className="App">
      <h1>DApp todo List | </h1> 
      <h4>current account : {accounts[0]}</h4>
      <button onClick={async() => await connectWallet(provider)}>Connect</button>

      { RegisterWalletButton }
      { AddTaskInterface }

      <div>
        <h3>tasks: </h3>

        { TasksRepresentation }

      </div>
    </div>
  );
}

