// ethers.BigNumber.from(task[1]).toNumber()
const{ ethers } = require('ethers');

export const loadTasks = async (contract)=>{
    const tasks = await contract.getTasks();
    await tasks.wait();
    return tasks.map(task => [task[0], ethers.BigNumber.from(task[1]).toNumber()]);
}


const addTask = async (contract, title) =>{
    const addTransaction = contract.addTask(title);
}

const removeTask = async (contract, id) =>{
    contract.removeTask(id, -1);    
}

const completeTask = async (contract, id) =>{
    contract.removeTask(id, 1);
}
