import logo from './logo.svg';
import './App.css';
import { useState } from 'react';
import { useEffect } from 'react';
import { useRef } from 'react';
import React from 'react';
import { ethers } from 'ethers';
import { loadTasks, addTask } from './_logics';

function App() {
  const [tasks, setTasks] = useState([]);
  const [accounts, setAccounts] = useState([]);
  const [wallet, setWallet] = useState(null);
  const [contract, setContract] = useState(null);
  const [provider, setProvider] = useState(null);

  const newTaskTitle = useRef(0);


  const contractAdress = '0x4C506b4eBd7C640853bc83FF779F2722185D8Cd4';
  const contractAbi = [
    {
      "inputs": [
        {
          "internalType": "string",
          "name": "title",
          "type": "string"
        }
      ],
      "name": "addTask",
      "outputs": [],
      "stateMutability": "nonpayable",
      "type": "function"
    },
    {
      "anonymous": false,
      "inputs": [
        {
          "indexed": false,
          "internalType": "string",
          "name": "",
          "type": "string"
        }
      ],
      "name": "CompletedTask",
      "type": "event"
    },
    {
      "anonymous": false,
      "inputs": [
        {
          "indexed": false,
          "internalType": "string",
          "name": "",
          "type": "string"
        }
      ],
      "name": "RemovedTask",
      "type": "event"
    },
    {
      "anonymous": false,
      "inputs": [
        {
          "indexed": false,
          "internalType": "string",
          "name": "",
          "type": "string"
        }
      ],
      "name": "newTask",
      "type": "event"
    },
    {
      "inputs": [
        {
          "internalType": "int256",
          "name": "id",
          "type": "int256"
        },
        {
          "internalType": "int256",
          "name": "rType",
          "type": "int256"
        }
      ],
      "name": "removeTask",
      "outputs": [],
      "stateMutability": "nonpayable",
      "type": "function"
    },
    {
      "inputs": [],
      "name": "getTasks",
      "outputs": [
        {
          "components": [
            {
              "internalType": "string",
              "name": "title",
              "type": "string"
            },
            {
              "internalType": "int256",
              "name": "id",
              "type": "int256"
            }
          ],
          "internalType": "struct ToDoList.Task[]",
          "name": "",
          "type": "tuple[]"
        }
      ],
      "stateMutability": "view",
      "type": "function"
    }
  ]

  const filter = {
    address: accounts[0],
    topics: [
      ethers.utils.id("newTask(string)")
    ]
}

  useEffect(()=>{

    const provider = new ethers.providers.Web3Provider(window.ethereum);

    setProvider(provider);
  }, [])


  const connectWallet = async()=>{

    const tempAccounts = await provider.send('eth_requestAccounts', []);

    const newContract = new ethers.Contract(contractAdress, contractAbi, provider);

    provider.on(filter, async (title)=>{
      const loadedTasks = await loadTasks(newContract);

      setTasks( loadedTasks );
    });

    console.log(provider.listeners("newTask"))

    // setProvider(provider);
    // console.log(newProvider);
    setContract(newContract);
    setAccounts(tempAccounts);

    const loadedTasks = await loadTasks(newContract);
    setTasks( loadedTasks );

  }


  return (
    <div className="App">
      <h1>DApp todo List | </h1> 
      <h4>current account : {accounts[0]}</h4>
      <button onClick={async() => await connectWallet()}>Connect</button>
      <div>
        <input ref={newTaskTitle} placeholder='enter task'/> 
        <button onClick={()=>{
          const signer = provider.getSigner();
          // console.log(signer)
          console.log(provider)

          addTask( new ethers.Contract(contractAdress, contractAbi, signer), newTaskTitle.current.value);
          newTaskTitle.current.value = '';
        }}>add task</button>
      </div>
      <div>
        <h3>tasks: </h3>
        {
          tasks.map(task => <h4>{task[0]} | <span onClick={()=>alert('task completed')}>Done</span> <span onClick={()=>alert('task removed')}>Del</span></h4>)
        }
      </div>
    </div>
  );
}

export default App;
