import logo from './logo.svg';
import './App.css';
import { useState } from 'react';
import { useEffect } from 'react';
import React from 'react';


function App() {
  const [tasks, setTasks] = useState([])

  useEffect(()=>{
    setTasks([["title", 1]]);
  }, [])


  return (
    <div className="App">
      <h1>DApp todo List</h1>
      <button>Connect</button>
      <div>
        <h3>tasks: </h3>
        {
          tasks.map(task => <h4>task[0]</h4>)
        }
      </div>
    </div>
  );
}

export default App;
