import logo from './logo.svg';
import './App.css';
import { useState } from 'react';
import { useEffect } from 'react';
import { useRef } from 'react';
import React from 'react';
import { ethers } from 'ethers';
import { loadTasks, addTask } from './_logics';

function App() {
  const [tasks, setTasks] = useState([]);
  const [accounts, setAccounts] = useState([]);
  const [wallet, setWallet] = useState(null);
  const [contract, setContract] = useState(null);
  const [provider, setProvider] = useState(null);

  const newTaskTitle = useRef(0);


  const contractAdress = '0xAdA49b192Ed3dB8dC62B9a990A6613dC3a294a6b';
  const contractAbi = [
      'function addTask(string memory title) public',
      'function removeTask(int id, int rType) public',
      'function getTasks() public view returns (tuple(string, int)[] memory)',
      'event newTask(string)'
  ]
  filter = {
    address: "dai.tokens.ethers.eth",
    topics: [
        utils.id("newTask(string)")
    ]
}

  useEffect(()=>{
    console.log(window.ethereum);
  }, [])


  const connectWallet = async()=>{
    const newProvider = new ethers.providers.Web3Provider(window.ethereum);

    newProvider.on('newTask', async (title)=>{
      const loadedTasks = await loadTasks(newContract);
      setTasks( loadedTasks );
    })

    setProvider(newProvider);

    const tempAccounts = await newProvider.send('eth_requestAccounts', []);

    const newContract = new ethers.Contract(contractAdress, contractAbi, newProvider);
    setContract(newContract);
    setAccounts(tempAccounts);

    const loadedTasks = await loadTasks(newContract);
    setTasks( loadedTasks );

  }


  return (
    <div className="App">
      <h1>DApp todo List | </h1> 
      <h4>current account : {accounts[0]}</h4>
      <button onClick={async() => await connectWallet()}>Connect</button>
      <div>
        <input ref={newTaskTitle} placeholder='enter task'/> 
        <button onClick={()=>{
          const signer = provider.getSigner();
          console.log(signer)
          addTask( new ethers.Contract(contractAdress, contractAbi, signer), newTaskTitle.current.value);
          newTaskTitle.current.value = '';
        }}>add task</button>
      </div>
      <div>
        <h3>tasks: </h3>
        {
          tasks.map(task => <h4>{task[0]}</h4>)
        }
      </div>
    </div>
  );
}

export default App;
